</template>

<script>
  /* eslint-disable camelcase,one-var,no-undef,no-unused-vars */
  import ScrollToPlugin from 'gsap/ScrollToPlugin'
  import $ from 'jquery'
  import 'jquery-validation'
  export default {
    name: 'Home',
    metaInfo: {
      title: 'Nectar\'s Fresh, Natural and NZ made Beverages',
      meta: [
        {
          name: 'description',
          content: 'Nectar\'s fresh, delicious and natural Tea beverages supplied right to your store.'
        }
      ]
    },
    mounted () {
      TweenMax.delayedCall(0.5, this.animate_logo)
      $(window).scrollTop(0)
      window.removeEventListener('scroll', this.check_scrolled)
      window.removeEventListener('mousewheel', this.check_scrolled)
      window.addEventListener('scroll', this.check_scrolled)
      window.addEventListener('mousewheel', this.check_scrolled)
    },
    methods: {
      scroll_down () {
        this.auto_scroll_index(true)
        this.clickSound.play()
      },
      check_scrolled () {
        if (!this.index_auto_scrolled) {
          window.removeEventListener('scroll', this.check_scrolled)
          window.removeEventListener('mousewheel', this.check_scrolled)
          this.index_auto_scrolled = true
        }
      },
      auto_scroll_index (click) {
        let $footer = $('footer'),
          $btn = $('._title .-btn'),
          $riseFade = $('._title h1, ._title p, ._title .-btn')
        if (this.index_auto_scrolled && !click) {
          return
        }
        this.index_auto_scrolled = true
        new TimelineMax()
          .set($riseFade, {autoAlpha: 0})
          .to(window, 0.7, {
            scrollTo: {
              y: window.innerHeight
            }
          })
          .staggerFromTo($riseFade, 0.3, {y: 20, autoAlpha: 0}, {y: 0, autoAlpha: 1}, 0.2)
          .staggerTo($btn, 0.2,
            {
              backgroundColor: 'black',
              color: 'white',
              yoyo: true,
              repeat: 1
            }, 0.5)
          .to($footer, 0.43, {y: -($footer.outerHeight() / 2), yoyo: true, repeat: 1})
          .set($btn, {clearProps: 'color,backgroundColor'})
      },
      bird_chirp () {
        let animation_start = document.getElementById('animation-start'),
          animation_reverse = document.getElementById('animation-reverse'),
          svg = document.getElementsByClassName('logo')[0]

        if (!animation_start.beginElement) {
          return
        }
        if (!svg.classList.contains('saved')) {
          svg.classList.add('saved')
          animation_start.beginElement()
        } else {
          svg.classList.remove('saved')
          animation_reverse.beginElement()
        }
      },
      animate_logo () {
        if (this.chirped) {
          return
        }
        this.chirped = true
        let $logo = $('.-svg-logo'),
          $menu = $('.menu-icon'),
          $bird = $logo.find('.svg-bird').children(),
          $rings = $logo.find('.svg-rings').children(),
          $title = $logo.find('.svg-title').children(),
          $sub_title = $logo.find('.svg-sub-title').children(),
          $lines = $logo.find('.svg-lines').children(),
          $scroll_button = $('.-scroll-arrow'),
          all = [$bird, $rings, $title, $sub_title, $lines]

        let config = {
            fade_dur: 0.45,
            fade_del: 0.1,
            stagger: 0.06,
            y: 40,
            x: 60
          },
          sound_points = [
            0.083,
            0.188,
            0.3,
            0.4,
            0.5,
            0.66,
            0.73,
            0.8,
            0.9,
            0.99,
            1.21,
            1.377,
            1.56,
            1.67,
            1.75,
            1.875
          ],
          chirp = new TimelineMax()

        sound_points.forEach((point) => {
          chirp.add(this.bird_chirp, point)
        })
        // this.chirpSound.play()
        new TimelineMax()
          .set(all, {autoAlpha: 0})
          .set($logo, {autoAlpha: 1})
          .fromTo($bird, config.fade_dur,
            {
              y: config.y
            },
            {
              ease: Quart.easeIn,
              autoAlpha: 1,
              y: 0
            }, '+=0.2')
          .call(($this) => {
            $this.chirpSound.play()
          }, [this])
          .fromTo($rings, config.fade_dur,
            {
              y: config.y
            },
            {
              autoAlpha: 1,
              y: 0
            }, '-=' + config.fade_del)
          .staggerFromTo($title, config.fade_dur / 3,
            {
              y: config.y / 2
            },
            {
              ease: Cubic.easeOut,
              autoAlpha: 1,
              y: 0
            }, config.fade_dur / 5, '-=' + config.fade_del)
          .staggerFromTo($sub_title, config.fade_dur / 3,
            {
              y: config.y / 2
            },
            {
              ease: Cubic.easeOut,
              autoAlpha: 1,
              y: 0
            }, config.fade_dur / 5, '-=' + config.fade_del)
          .fromTo($($lines[0]), config.fade_dur * 2,
            {
              x: config.x
            },
            {
              ease: Cubic.easeOut,
              autoAlpha: 1,
              x: 0
            }, 'lines-=' + config.fade_del)
          .fromTo($($lines[1]), config.fade_dur * 2,
            {
              x: -config.x
            },
            {
              ease: Cubic.easeOut,
              autoAlpha: 1,
              x: 0
            }, 'lines-=' + config.fade_del)
          .fromTo($scroll_button, config.fade_dur,
            {
              y: config.y,
              scaleX: 0.5
            },
            {
              ease: Cubic.easeOut,
              autoAlpha: 1,
              scaleX: 1,
              y: 0
            }, 'end')
          // .to($menu || '.menu-icon', 0.288, {left: '100%', autoAlpha: 1})
          .set([$scroll_button], {className: '+=__active'})
          .set([$scroll_button], {className: '-=__active'}, '+=0.5')
          .call(this.auto_scroll_index, [], this, '+=0.6')
          .to($scroll_button, config.fade_dur,
            {
              ease: Cubic.easeIn,
              y: 15,
              scaleX: 1.1,
              scaleY: 0.9,
              repeat: -1,
              yoyo: true
            }, 'end+=' + config.fade_dur)
      }
    }
  }
</script>

<style lang="scss" scoped>
  ._full {
    /*width: 100vw;*/
    height: 100vh
  }
  #index {
    min-height: 100vh
  }
</style>
